/** @jsxImportSource @emotion/react */
import { css } from '@emotion/react';
import React from 'react';

type NodeId = string | number;

type TreeNode = {
  id: NodeId;
  label: string;
  children?: TreeNode[];
};

export type TreeProps = {
  data: TreeNode[];
  onNodeClick: (node: TreeNode) => void;
};

export function Tree({ data, onNodeClick }: TreeProps) {
  const [expandedNodes, setExpandedNodes] = React.useState<Set<NodeId>>(new Set());

  const toggleNode = (nodeId: NodeId) => {
    setExpandedNodes((prev) => {
      const newSet = new Set(prev);
      if (newSet.has(nodeId)) {
        newSet.delete(nodeId);
      } else {
        newSet.add(nodeId);
      }
      return newSet;
    });
  };

  const renderNode = (node: TreeNode) => {
    const isExpanded = expandedNodes.has(node.id);
    return (
      <div key={node.id}>
        <div css={styles.nodeElement}>
          <span
            className={isExpanded ? 'expanded' : undefined}
            onClick={() => toggleNode(node.id)}
            css={[styles.toggle, !node.children ? styles.hideToggle : null]}
          >
            â–¶
          </span>
          <span onClick={() => onNodeClick(node)} css={styles.labelContent}>
            {node.label}
          </span>
        </div>
        {isExpanded && node.children && <div css={styles.children}>{node.children.map(renderNode)}</div>}
      </div>
    );
  };

  return <div>{data.map(renderNode)}</div>;
}

const styles = {
  labelContent: css`
    padding-left: 4px;
    padding-right: 4px;
    border-radius: 4px;
    cursor: pointer;
    &:hover {
      background-color: rgba(0, 0, 0, 0.05);
    }
  `,
  children: css`
    margin-left: 20px;
  `,
  toggle: css`
    font-size: 0.5rem;
    cursor: pointer;
    transition: transform 200ms ease-in-out;

    &.expanded {
      transform: rotate(90deg);
    }
  `,
  nodeElement: css`
    display: flex;
    align-items: center;
  `,
  hideToggle: css`
    visibility: hidden;
  `,
};
